<?php include_once "../Module/PhCFG.php" ?><?php include_once "../Module/MySQL.php" ?><?php include_once "../Module/PhFunctions.php" ?><?php include_once "../Module/CpyFunctions.php" ?><?php$dbName = ph_Session('dbName');if ($dbName === null || $dbName === '') {  ph_SetSession('dbName', PHS_SMB_ADMIN);}ph_PrepareGets();ph_PreparePosts();$vCopy = '';$vPage = '';$vParams = '';$aURI = getURIArray();if (count($aURI) >= (PHS_URI_IDX + 1) && isset($aURI[PHS_URI_IDX + 1])) {  $vCopy = $aURI[PHS_URI_IDX + 1];}if (count($aURI) >= (PHS_URI_IDX + 2) && isset($aURI[PHS_URI_IDX + 2])) {  $vPage = $aURI[PHS_URI_IDX + 2] . '.php';}if (count($aURI) >= (PHS_URI_IDX + 3) && isset($aURI[PHS_URI_IDX + 3])) {  $vParams = $aURI[PHS_URI_IDX + 3];}$aParams = explode('|', urldecode($vParams));$oCopy = cPhsCpy::getInstanceByGId($vCopy);if ($oCopy->Id !== -999) {  global $conn;  $conn = ph_Connect($oCopy->dbName);} else {  $vPage = 'page404.php';}$vLang = 'en';include_once "../Module/PhLabels-" . $vLang . ".php";initLabels();$vMainPage = 'main.php';include $vMainPage;