<?php$aStatus = cPhsCode::getArray(cPhsCode::STATUS);$aCustomizes = cPhsCustomize::getArray();?><div class="subheader py-2 py-lg-4 subheader-solid" id="kt_subheader">  <div class="container-fluid">    <div class="row pt-2">      <div class="col col-md-3 pt-4">        <h5 class="text-dark font-weight-bold"><?php echo getLabel($requestProg->Name); ?></h5>        <div class="subheader-separator subheader-separator-ver bg-gray-200"></div>      </div>      <div class="col col-md-6 pt-2">      </div>      <div class="col cil-md-3 pt-2 text-right">      </div>    </div>  </div></div><div class="d-flex flex-column-fluid">  <div class="container">    <div class="row">      <div class="col-12 col-sm-10 col-md-8 card mx-auto py-3">        <?php        foreach ($aCustomizes as $element) {          $nStatus = 1;          ?>          <div class="row pt-1">            <div class="col-12 col-sm-2 text-center text-sm-right px-1">              <label class="col-form-label text-lg-right text-left"><?php echo getLabel($element->Name); ?></label>            </div>            <div class="col-2 col-sm-1 text-center px-1 form-group form-inline">              <span class="switch switch-sm switch-outline switch-icon switch-info">                <label>                  <input id="status-<?php echo $element->Id; ?>" type="checkbox" <?php echo ($nStatus == 1 ? 'checked="checked"' : ''); ?> >                  <span></span>                </label>              </span>              <label for="status-<?php echo $element->Id; ?>"></label>            </div>            <div class="col-8 col-sm-8 text-left px-1">              <?php              switch ($element->TypeId) {                case 1: // Number                  ?>                  <input id="field-<?php echo $element->Id; ?>" class="form-control form-control-sm" type="text" value="<?php echo $element->Value; ?>">                  <?php                  break;                case 2: // Varchar                  ?>                  <input id="field-<?php echo $element->Id; ?>" class="form-control form-control-sm" type="text" value="<?php echo $element->Value; ?>">                  <?php                  break;                case 3: // Date                  ?>                  <div class="input-group date">                    <input id="field-<?php echo $element->Id; ?>" class="form-control form-control-sm ph_datepicker" type="text" value="<?php echo date('Y-m-d'); ?>"/>                    <div class="input-group-append datepicker-btn">                      <span class="input-group-text">                        <i class="la la-calendar"></i>                      </span>                    </div>                  </div>                  <?php                  break;                case 4: // Time                  ?>                  <div class="input-group timepicker">                    <input id="field-<?php echo $element->Id; ?>" class="form-control form-control-sm ph_timepicker"  placeholder="Select time" type="text">                    <div class="input-group-append datepicker-btn">                      <span class="input-group-text">                        <i class="la la-clock-o"></i>                      </span>                    </div>                  </div>                  <?php                  break;                case 5: // Datetime                  ?>                  <div class="input-group date">                    <input id="field-<?php echo $element->Id; ?>" class="form-control form-control-sm ph_datetimepicker" type="text" value="<?php echo date('Y-m-d'); ?>"/>                    <div class="input-group-append datepicker-btn">                      <span class="input-group-text">                        <i class="la la-calendar"></i>                      </span>                    </div>                  </div>                  <?php                  break;                case 6: // Select                  $vSQL = 'SELECT `' . $element->Idfld . '` AS `id`, `' . $element->Namefld . '` AS `name`'                    . ' FROM ' . $element->TableName;                  if ($element->Cond != null && $element->Cond != '') {                    $vSQL .= ' WHERE (' . $element->Cond . ')';                  }                  if ($element->Order != null && $element->Order != '') {                    $vSQL .= ' ORDER BY (' . $element->Order . ')';                  }                  $res = ph_Execute($vSQL);                  if ($res != '') {                    ?>                    <select id="field-<?php echo $element->Id; ?>" class="form-control form-control-sm form-select">                      <?php                      $selected = '';                      while (!$res->EOF) {                        if ($res->fields('id') == $element->Value) {                          $selected = 'selected';                        }                        ?>                        <option value="<?php echo $res->fields('id'); ?>" <?php echo $selected; ?>><?php echo $res->fields('name'); ?></option>                        <?php                        $selected = '';                        $res->MoveNext();                      }                      $res->Close();                      ?>                    </select>                    <?php                  }                  break;                case 7: // Autocomplete                  ?>                  <input id="field-<?php echo $element->Id; ?>" type="hidden" value="<?php echo $element->Value; ?>">                  <input id="fieldName<?php echo $element->Id; ?>" class="form-control form-control-sm phAutocomplete" data-acoperation="<?php echo $element->TableName; ?>" data-acrel="field-<?php echo $element->Id; ?>">                  <?php                  break;                case 8: // PhsCode                  ?>                  <select id="field-<?php echo $element->Id; ?>" class="form-control form-control-sm form-select">                    <?php                    $aArray = cPhsCode::getArray($element->TableName);                    foreach ($aArray as $option) {                      $selected = '';                      if ($element->Value == $option->Id) {                        $selected = 'selected';                      }                      ?>                      <option value="<?php echo $option->Id; ?>" <?php echo $selected; ?>><?php echo $option->Name; ?></option>                      <?php                      $selected = '';                    }                    ?>                  </select>                  <?php                  break;                case 9: // CpyCode                  ?>                  <select id="field-<?php echo $element->Id; ?>" class="form-control form-control-sm form-select">                    <?php                    $aArray = cCpyCode::getArray($element->TableName);                    foreach ($aArray as $option) {                      $selected = '';                      if ($element->Value == $option->Id) {                        $selected = 'selected';                      }                      ?>                      <option value="<?php echo $option->Id; ?>" <?php echo $selected; ?>><?php echo $option->Name; ?></option>                      <?php                      $selected = '';                    }                    ?>                  </select>                  <?php                  break;                default:                  break;              }              ?>            </div>            <div class="col-2 col-sm-1 text-left px-1">              <button class="btn btn-sm btn-light-primary form-control form-control-sm customize-save" data-rid="<?php echo $element->Id; ?>" data-toggle="tooltip" title="<?php echo getLabel('Save'); ?>">                <i class="icon-xl far fa-save"></i>              </button>            </div>          </div>          <?php        }        ?>      </div>    </div>  </div></div>