<div class="modal fade" id="changeImageModal" tabindex="-1" role="dialog" aria-labelledby="changeImageModal" aria-hidden="true">  <div class="modal-dialog modal-dialog-centered modal-sm" role="document">    <div class="modal-content">      <div class="modal-header">        <h5 class="modal-title" id="changeImageModalLabel"><?php echo getLabel('Change Image'); ?></h5>        <button type="button" class="close" data-dismiss="modal" aria-label="Close">          <i aria-hidden="true" class="ki ki-close"></i>        </button>      </div>      <div class="modal-body">        <form id="ph_ChangeImage_form">          <div class="row">            <div class="col-12 col-sm-6 m-auto">              <div class="w-100 p-0">                <label for="fldFile" class="btn btn-xs btn-icon btn-circle btn-white btn-hover-text-primary btn-shadow" style="position: absolute; right: 0px; top: -10px;" data-toggle="tooltip" title="" data-original-title="Change image">                  <i class="fa fa-pen icon-sm text-muted"></i>                </label>                <input id="fldFile" type="file" class="fileField d-none" value="" data-previewer="attPreview" data-relfld="fldAttach" data-filename="fldFileName"  data-relname="fldFName" data-relext="fldFExt" data-folder="item">                <input id="fldFName" type="hidden" value="">                <input id="fldFileName" type="hidden" value="">                <input id="fldFExt" type="hidden" value="">                <input id="fldAttach" type="hidden" value="">                <img id="attPreview" class="border border-info border-1" src="" style="width: 100%; min-height: 185px;">                <span class="btn btn-xs btn-icon btn-circle btn-white btn-hover-text-primary btn-shadow attache-clear" style="position: absolute; right: 0px; bottom: -10px;" data-rfield="fldFile" data-toggle="tooltip" title="Cancel image">                  <i class="ki ki-bold-close icon-xs text-muted"></i>                </span>              </div>            </div>          </div>        </form>      </div>      <div class="modal-footer">        <button type="button" class="btn btn-light-warning font-weight-bold text-center pl-4 pr-2" data-dismiss="modal"><i class="icon-2x flaticon2-cancel"></i></button>        <button id="ph_ChangeImage_submit" type="button" class="btn btn-primary btn-change-password font-weight-bold text-center pl-4 pr-2"><i class="icon-2x flaticon2-check-mark"></i></button>      </div>    </div>  </div></div>