<div class="modal fade" id="changePasswordModal" tabindex="-1" role="dialog" aria-labelledby="changePasswordModal" aria-hidden="true">  <div class="modal-dialog modal-dialog-centered modal-md" role="document">    <div class="modal-content">      <div class="modal-header">        <h5 class="modal-title" id="changePasswordModalLabel"><?php echo getLabel('Change Password'); ?></h5>        <button type="button" class="close" data-dismiss="modal" aria-label="Close">          <i aria-hidden="true" class="ki ki-close"></i>        </button>      </div>      <div class="modal-body">        <form id="ph_ChangePassword_form">          <div class="row">            <div class="col-12">              <div class="form-group row">                <label class="col-form-label col-4 text-lg-right text-left"><?php echo getLabel('Current Password'); ?></label>                <div class="col-8">                  <input class="form-control form-control-lg form-control-solid mb-1" type="password" name="opassword" id="opassword" value="" required="true"/>                </div>              </div>              <div class="form-group row">                <label class="col-form-label col-4 text-lg-right text-left"><?php echo getLabel('New Password'); ?></label>                <div class="col-8">                  <input class="form-control form-control-lg form-control-solid" type="password" name="npassword" id="npassword" value="" required="true"/>                </div>              </div>              <div class="form-group row">                <label class="col-form-label col-4 text-lg-right text-left"><?php echo getLabel('Verify Password'); ?></label>                <div class="col-8">                  <input class="form-control form-control-lg form-control-solid" type="password"  name="vpassword" id="vpassword" value="" required="true"/>                </div>              </div>            </div>          </div>        </form>      </div>      <div class="modal-footer">        <button type="button" class="btn btn-light-warning font-weight-bold text-center pl-4 pr-2" data-dismiss="modal"><i class="icon-2x flaticon2-cancel"></i></button>        <button id="ph_ChangePassword_submit" type="button" class="btn btn-primary btn-change-password font-weight-bold text-center pl-4 pr-2"><i class="icon-2x flaticon2-check-mark"></i></button>      </div>    </div>  </div></div>