<?php$aLangs = cPhsLang::getArray('id!="' . $oLang->Id . '"');if (count($aLangs)) {  ?>  <div class="dropdown d-block">    <div class="topbar-item pt-4" data-toggle="dropdown" data-offset="55px,5px">      <div class="btn btn-clean btn-dropdown btn-md">        <span id="current-language" class='current-language' data-code='<?php echo $oLang->Code; ?>'><?php echo $oLang->Code; ?></span>      </div>    </div>    <div class="dropdown-menu p-0 m-0 dropdown-menu-anim-up dropdown-menu-sm dropdown-menu-right">      <ul class="navi navi-hover py-4">        <?php        foreach ($aLangs as $lang) {          ?>          <li class="navi-item">            <span class="navi-link change-language" data-code='<?php echo $lang->Code; ?>' data-dir='<?php echo $lang->Direction; ?>' data-name='<?php echo $lang->Code . ' - ' . $lang->Name; ?>'>              <span class="navi-text"><?php echo $lang->Code . ' - ' . $lang->Name; ?></span>            </span>          </li>          <?php        }        ?>      </ul>    </div>  </div>  <?php}?>