<?php$aPeriods = cCpyWPeriod::getArray('status_id=1 AND id!="' . $oWorkperiod->Id . '"');if (count($aPeriods)) {  ?>  <div class="dropdown d-block">    <div class="topbar-item pt-4" data-toggle="dropdown" data-offset="55px,5px">      <div class="btn btn-clean btn-dropdown btn-md">        <span id="current-period" class='current-period' data-id='<?php echo $oWorkperiod->Id; ?>'><?php echo $oWorkperiod->Name; ?></span>      </div>    </div>    <div class="dropdown-menu p-0 m-0 dropdown-menu-anim-up dropdown-menu-sm dropdown-menu-right">      <ul class="navi navi-hover py-4">        <?php        foreach ($aPeriods as $period) {          ?>          <li class="navi-item">            <span class="navi-link change-period" data-id='<?php echo $period->Id; ?>' data-name='<?php echo $period->Name; ?>'>              <span class="navi-text"><?php echo $period->Name; ?></span>            </span>          </li>          <?php        }        ?>      </ul>    </div>  </div>  <?php}?>